const strip = input => {
  const chars = input.split("");
  let text = true;
  const filteredChars = chars.filter(char => {
    if (char === "<") {
      text = false;
    } else if (char === ">") {
      text = true;
      return false;
    };
    return text;
  });
  return filteredChars.join("");
};

const newlines = [
  "<br />",
  "</p>",
  "</h1>",
  "</h2>",
  "</h3>",
  "</h4>",
  "</h5>",
  "</h6>"
];
const symbols = [
  { in: "&nbsp;", out: " " },
  { in: "&mdash;", out: "—"}
];
const process = string => {
  newlines.forEach(symbol => {
    string = string.replaceAll(symbol, "\n");
  });
  symbols.forEach(replacement => {
    string = string.replaceAll(replacement.in, replacement.out);
  });
  return strip(string);
};
