  let output = `\n\t${comment.comment}\n\t<i>От: ${comment.author}</i>${processAttachments(comment)}`;
  return output;
};
const oldIDs = getIDs($input.item.json.old.subs);
const newIDs = getIDs($input.item.json.new.subs);
const flags = {
  new: false,
  edited: false,
  deleted: false
};
// console.log(`Old comment IDs are ${JSON.stringify(oldIDs)}, new IDs are ${JSON.stringify(newIDs)}`);
$input.item.json.message = `В заявке <a href="https://help.knp24.ru/request/${$input.item.json.new.id}">№ ${$input.item.json.new.id}</a> `;
