const editedComments = [];
const deletedComments = [];
newIDs.forEach(id => {
  if (! $input.item.json.old.subs[id]) {
    flags.new = true;
    newComments.push(id);
  } else if (
    $input.item.json.old.subs[id] &&
    typeof $input.item.json.old.subs[id] === 'object' &&
    $input.item.json.old.subs[id] !== null &&
    JSON.stringify($input.item.json.old.subs[id]) !== JSON.stringify($input.item.json.new.subs[id])
  ) {
    flags.edited = true;
    editedComments.push(id);
  };
});
oldIDs.forEach(id => {
  if (! $input.item.json.new.subs[id]) {
    flags.deleted = true;
    deletedComments.push(id);
  }
});
if (flags.new && newComments.length === 1) {
  $input.item.json.message += `добавлен комментарий:${processSingle($input.item.json.new.subs[newComments[0]])}`;
} else if (flags.new && newComments.length > 1) {
  $input.item.json.message += `добавлены комментарии:${processMultiple($input.item.json.new.subs, newComments)}`;
};
if (flags.edited) {
  if (flags.new) {
    $input.item.json.message += "\nТакже, ";
  };
  if (editedComments.length === 1) {
    $input.item.json.message += `был отредактирован один из комментариев. Было:${processSingle($input.item.json.old.subs[editedComments[0]])}\nСтало:${processSingle($input.item.json.new.subs[editedComments[0]])}`;
