    });
  }
};
if (flags.deleted) {
  if (flags.new || flags.edited) {
    $input.item.json.message += "\nА ещё ";
  };
  if (deletedComments.length === 1) {
    $input.item.json.message += `был удалён комментарий${processSingle($input.item.json.old.subs[deletedComments[0]])}`;
