    $input.item.json.message += `было удалено несколько комментариев${processMultiple($input.item.json.old.subs, deletedComments)}`;
  };
};
// console.log(`New comments ID are ${newComments.toString()}, edited ones are ${editedComments.toString()} and deleted ones are ${deletedComments.toString()}`);
