const updates = [];
const data = JSON.parse(JSON.stringify($input.all()));
const closed = data[0].json.closed;
const add = (request, type) => {
  updates.push({
    json: JSON.parse(JSON.stringify(request))
  });
  updates[updates.length - 1].json.type = type;
};
const unmark = request => {
  const output = JSON.parse(JSON.stringify(request));
  delete output.transferred;
  return output;
};
if (! Object.keys(data[1].json).length) {
  const requests = JSON.parse(JSON.stringify(data[data.length - 1].json.current));
  requests.forEach(request => add(request, "new"));
};
const db = data[1].json;
const current = data[data.length - 1].json.current;
const find = (requests, id) => {
  return requests.filter(request => {
    return request.id === id;
  });
};
const isOurs = request => {
  if (request.gfullname === "Группа МТО") {
    return false;
  } else if (request.gfullname === "Группа системного администрирования") {
    return true;
  } else if (
    request.service_name === "Группа УИТ - Отдел системного администрирования" &&
    request.gfullname === null
  ) {
    return true;
  } else {
    return false;
  };
};
current.forEach(request => {
  const dbEntry = db[request.id];
  if (
    request?.id &&
    ! dbEntry &&
    $runIndex === 0
  ) {
    if (! isOurs(request)) {
      return;
    };
    // console.log(`Found new request. DB entry is ${JSON.stringify(dbEntry)}, current data are ${JSON.stringify(request)}, ID is ${request.id}`);
    closed.includes(request.id) ? add(request, "reopened") : add(request, "new");
    return;
  };
  if (! isOurs(dbEntry) && isOurs(request) && $runIndex === 0) {
    add(request, "transferred_back");
  } else if (! isOurs(request) && dbEntry.transferred !== "true") {
    add(request, "transferred");
  } else if (
    isOurs(request) &&
    JSON.stringify(unmark(request)) !== JSON.stringify(request)
  ) {
    add(request, "updated");
  };
});
Object.keys(db).forEach(key => {
  if (
    key.length !== 6 ||
    ! Number.isInteger(Number.parseInt(key, 6)) ||
    closed.includes(key) ||
    $runIndex
  ) {
    return;
  };
  const match = find(current, key);
  console.log(`Key is ${key}, match is ${JSON.stringify(match)}`);
  if (
    match instanceof Array &&
    match.length === 0
  ) {
    add(db[key], "missed");
  }
});
return updates;

