// Code here will run only once, no matter how many input items there are.
// More info and help: https://docs.n8n.io/nodes/n8n-nodes-base.function
// Tip: You can use luxon for dates and $jmespath for querying JSON structures

const parseFrequency = tokens => {
  const numbers = new Map();
  numbers.set("один", 1);
  numbers.set("два", 2);
  numbers.set("три", 3);
  numbers.set("четыре", 4);
  numbers.set("пять", 5);
  numbers.set("шесть", 6);
  numbers.set("семь", 7);
  numbers.set("восемь", 8);
  numbers.set("девять", 9);
  numbers.set("десять", 10);
  numbers.set("одиннадцать", 11);
  numbers.set("двенадцать", 12);
  const units = new Map()
  units.set("миллисекунд", "milliseconds");
  units.set("секунд", "seconds");
  units.set("минут", "minutes");
  units.set("час", "hours");
  units.set("день", "days");
  units.set("дня", "days");
  units.set("недел", "weeks");
  units.set("месяц", "months");
  units.set("квартал", "quarters");
  units.set("год", "years");
  units.set("лет", "years");
  let number = 1;
  let unit = undefined;

  for(let i = 0; i < tokens.length; i++) {
    if (Number.isInteger(Number.parseInt(tokens[i]))) {
      number = tokens[i];
      continue;
    };
    if (numbers.has(tokens[i])) {
      number = numbers.get(tokens[i]);
      continue;
    };
    for(let i2 = tokens[i].length; i2 > 0; i2--) {
      if (units.has(tokens[i].slice(0, i2))) {
        unit = units.get(tokens[i].slice(0, i2))
        break;
      }
    }
  };
  const ret = {};
  ret[unit] = number;
  return ret;
};

const getFrequency = humanReadable => {
  const tokens = humanReadable.split(" ").map(token => token.toLowerCase());
  if (tokens[0] === "раз" && tokens[1] === "в") {
    return parseFrequency(tokens.slice(2));
  }
  if (tokens[0].slice(0, 4) === "кажд") {
    return parseFrequency(tokens.slice(1));
  }
};

  const tasks = items[0].json.results.map(task => {
    const fieldNames = $node["Глобальные значения"].json.strings;
    let lastExec;
    if (task.properties[fieldNames.lastExec]) {
      lastExec = DateTime.fromFormat(task.properties[fieldNames.lastExec].date.start, "yyyy-MM-dd", {zone: 'utc'});
    }
    const frequency = getFrequency(task.properties[fieldNames.frequency].select.name);
    // const nextExec = lastExec ? : DateTime.now();
    // return {
    //   id: task.id,
    //   lastExecution: lastExec,
    //   nextExecution: 
    // };
    return lastExec;
  });
return { frequency: tasks };
